import initial from "./initial.js";
import types from "../actions/actionTypes";

const setInitial = () => initial;

const addPlayers = (state, action) => ({...state, players: state.players})

let players = (state) => ({ ...state, players: state.players }); 	

const reducer = (state, action) => { 
	switch (action.type) { 
		case types.ADD_PLAYER: return (players(state)); 
		default: return state;
	}
};

export default reducer;
