import initial from "../initial";

const setInitial = () => initial;

const addPlayer = (state, action) => ({ 
	...state, 
	players: [action.player, ...state.players] 
})

// const default (state, action) => ({
// 	...state,
// 	players: [action.player, ...state.players],
// })

// const setPlayer = (state, action) => {
// 	return { ...state, players: state.players };
// };
//can use destructuring to tidy up properties of the action p21 redux notes

const reducer = (state, action) => { 
	switch (action.type) { 
		case "addPlayer": return (addPlayer(state, action)); 
		case "reset": return setInitial();
		default: return state;
	}
};

export default reducer;















