import axios from "../../axios";

export const getTeams = () => (dispatch) => {
	axios.get("/").then(({ data }) => {
		dispatch(loaded(data.data));
	});
};

export const addPlayer = (player) => (dispatch) => {
	axios.put("/", {
		player
	}).then(({ data }) => {
		dispatch(player(data.data.player));
	});
};
